	created_at DATETIME,
	created_by VARCHAR,
	updated_at DATETIME,
	updated_by VARCHAR,
	CONSTRAINT USERS_PK PRIMARY KEY (id),
	CONSTRAINT users_FK FOREIGN KEY (created_by) REFERENCES users(id),
